    def graph_ver_by_ver(self, version: int) -> str | None:
        for info in self.build_versions:
            if version == info["version"]:
                return info.get("graph_ver", None)
        return None

    def scr_ver_by_version(
        self,
        version: int,
    ) -> int | None:  # returns script_version by version
        for info in self.build_versions:
            if version == info["version"]:
                return info.get("script_version", None)
        return None

    def build_by_version(
        self,
        version: int,
        script_version: int,
    ) -> str | None:  # returns build number by version
        for info in self.build_versions:
            if (version == info["version"]) and (
                script_version == info.get("script_version", None)
            ):
                return info["build"]
        return None

    def graph_build(self, version: int, script_version: int) -> str | None:
        # returns graph build (for graph unpacking) by version and script_version
        for info in self.build_versions:
            if (version == info["version"]) and (
                script_version == info.get("script_version", None)
            ):
                return info.get("graph_build")
        return None
