# trigslink/ascii_logo.py

def colorize_ascii(plain_ascii):
    result = []
    for char in plain_ascii:
        if char == "%":
            result.append("\033[38;5;18m%\033[0m") 
        elif char == "@":
            result.append(" ")  # Make @ "transparent"
        else:
            result.append(char)
    return "".join(result)

